import React, { useEffect, useState } from "react";
import Header from "./Components/Header";
import Recipes from "./Components/Recipes";
import axios from "axios";

function App() {
   const [search, setSearch] = useState("");
   const [newSearch, setNewSearch] = useState("");
   const [recipes,setRecipes]=useState([]);

   const YOUR_APP_ID="135ac2ff";
   const YOUR_APP_KEY="d6da505dcf58622685f944d3aee4819a";
  //  "https://api.edamam.com/search?q=chicken&app_id=${YOUR_APP_ID}&app_key=${YOUR_APP_KEY}

  useEffect( async ()=>{
     const res= await axios.get(`https://api.edamam.com/search?q=chicken&app_id=${YOUR_APP_ID}&app_key=${YOUR_APP_KEY}`);

    setRecipes()
     console.log(res.data.hits);
  },[])

   const ClickEvent = () => {
      setNewSearch(search);
      console.log(newSearch);
   };
   const InputEvent = (e) => {
      setSearch(e.target.value);
   };
   return (
      <div className="App">
         <Header Search={search} inputEvent={InputEvent} click={ClickEvent} />
         <Recipes />
      </div>
   );
}

export default App;
